class EditorDialogCtrl {
	constructor(
		$scope, $filter,
		$tourApi, $mapApi) {
		'ngInject';
		this.$filter = $filter;
		this.LocationResource = $tourApi.location;
		this.BuildingResource = $tourApi.building;
		this.SceneResource = $tourApi.scene;
		this.CategoryResource = $mapApi.category;
		this.FeatureResource = $mapApi.feature;
		this.CollectionResource = $mapApi.collection;
		if (this.item.scene) {
			$scope.$applyAsync(() => {
				this.initCurrentScene();
			});
		}
		if (this.item.feature) {
			$scope.$applyAsync(() => {
				this.initCurrentFeature();
			});
		}
	}
	initCurrentCategory() {
		let _id;
		this.loadCategories(categories => {
			_id = this.feature.properties.category&&this.feature.properties.category.hasOwnProperty('_id') 
				? this.feature.properties.category._id
				: this.feature.properties.category;
			this.category = this.$filter('filter')(categories, { _id }, true)[0];
		});
	}
	initCurrentFeature() {
		let _id;
		this.loadFeatures(features => {
			_id = this.item.feature&&this.item.feature.hasOwnProperty('_id')
				? this.item.feature._id
				: this.item.feature;
			this.feature = this.$filter('filter')(features, { _id }, true)[0];
			this.initCurrentCategory();
		});
	}
	initCurrentScene() {
		let _id;
		this.loadScenes(scenes => {
			_id = this.item.scene.hasOwnProperty('_id')
				? this.item.scene._id
				: this.item.scene;
			this.scene = this.$filter('filter')(scenes, { _id }, true)[0];
			this.initCurrentBuilding();
		});
	}
	initCurrentBuilding() {
		let _id;
		this.loadBuildings(buildings => {
			_id = this.scene.parent.hasOwnProperty('_id')
				? this.scene.parent._id
				: this.scene.parent;
			this.building = this.$filter('filter')(buildings, { _id }, true)[0];
			this.initCurrentLocation();
		});
	}
	initCurrentLocation() {
		let _id;
		this.loadLocations(locations => {
			_id = this.building.parent.hasOwnProperty('_id')
				? this.building.parent._id
				: this.building.parent;
			this.location = this.$filter('filter')(locations, { _id }, true)[0];
		});
	}
	loadLocations(cb) {
		return this.LocationResource.query().$promise.then(locations => {
			this.locations = locations;
			cb&&cb(locations);
		});
	}
	loadBuildings(cb) {
		return this.BuildingResource.query(this.location ? {
			filter: {
				parent: this.location._id
			}
		} : null).$promise.then(buildings => {
			this.buildings = buildings;
			cb&&cb(buildings);
		});
	}
	loadScenes(cb) {
		return this.SceneResource.query(this.building ? {
			filter: {
				parent: this.building._id
			}
		} : null).$promise.then(scenes => {
			this.scenes = scenes;
			cb&&cb(scenes);
		});
	}
	loadCategories(cb) {
		return this.CategoryResource.query().$promise.then(categories => {
			this.categories = categories;
			cb&&cb(categories);
		});
	}
	loadFeatures(cb) {
		return this.FeatureResource.query(this.category ? {
			filter: {
				"properties.category": this.category._id
			}
		} : null).$promise.then(features => {
			this.features = features;
			cb&&cb(features);
		});
	}
	onChangeCategory() {
		this.feature = null;
	}
	onChangeFeature() {
		this.item.feature = this.feature;
	}
	onChangeScene() {
		this.item.scene = this.scene._id;
	}
	onChangeBuilding() {
		this.item.building = this.building._id;
		this.scene = null;
	}
	onChangeLocation() {
		this.item.location = this.location._id;
		this.building = null;
		this.scene = null;
	}
	//
}

export default {
  name: 'EditorDialogCtrl',
  fn: EditorDialogCtrl
};

const EDITOR_MESSAGES = {
	confirm: {
		textContent: 'Are you sure?',
		action: 'Confirm',
		highlightAction: true,
		highlightClass: 'md-warn',
		position: 'bottom left',
		hideDelay: 0
	},
	itemRemoved: {
		textContent: 'Item removed!',
		action: 'Publish changes',
		highlightAction: true,
		highlightClass: 'md-primary',
		position: 'bottom left',
		hideDelay: 5000
	},
	itemAdded: {
		textContent: 'Item added!',
		action: 'Publish changes',
		highlightAction: true,
		highlightClass: 'md-primary',
		position: 'bottom left',
		hideDelay: 5000
	},
	draftLoaded: {
		textContent: 'Draft loaded!',
		action: 'Publish changes',
		highlightAction: true,
		highlightClass: 'md-primary',
		position: 'bottom left',
		hideDelay: 5000
	},
	saveDraft: {
		textContent: 'Draft saved!',
		action: 'Publish changes',
		highlightAction: true,
		highlightClass: 'md-primary',
		position: 'bottom left',
		hideDelay: 5000
	},
	draftFound: {
		textContent: 'Saved drafts found!',
		action: 'Load most recent',
		highlightAction: true,
		highlightClass: 'md-primary',
		position: 'bottom left',
		hideDelay: 5000
	},
	publish: {
		textContent: 'Changes published!',
		action: 'Dismiss',
		position: 'bottom left',
		hideDelay: 5000
	},
	discardDraft: {
		textContent: 'Draft discarded!',
		action: 'Dismiss',
		position: 'bottom left',
		hideDelay: 1000
	},
	revertToDraft: {
		textContent: 'Reverted to last draft!',
		action: 'Dismiss',
		position: 'bottom left',
		hideDelay: 2000
	}
}

export default {
  name: 'EDITOR_MESSAGES',
  fn: EDITOR_MESSAGES
};

const DATA_MODELS = {
	scene: {
		name: '',
		code: '',
		// state: ''
		parent: '',
		panorama: ''//{}
		// sceneLinks: []
		// hotSpots: []
		// assets: []
		// entities: []
		// script: ''
	},
	sceneLinks: {
		scene: '',
		position: [
			0,
			0,
			0
		],
		rotation: [
			0,
			0,
			70
		]
	},
	hotSpots: {
		linked: false,
		name: '',
		desc: '',
		feature: '',
		position: [
			0,
			0,
			0
		]
	}
}

export default {
  name: 'DATA_MODELS',
  fn: DATA_MODELS
};

/**
 * The scene editor is an attachment for the scene
 * component that allows in-scene editing of scene elements.
 * It should only be rendered directly by the server, when
 * an admin user is logged in.
 * @example
 * <aframe-scene> <!-- required -->
 *   <scene-editor></scene-editor>
 * </aframe-scene>
 */
const sceneEditor = {
    controller: 'EditorCtrl',
    require: {
    	SceneCtrl: '^aframeScene'
    },
   	templateUrl: 'aframe/editor/_editor.html'
  };

export default sceneEditor;

import { element } from 'angular';

function EditorConfig($mdPanelProvider) {
  'ngInject';

	$mdPanelProvider.definePreset('editorDialog', {
    attachTo: element(document.body),
	  controller: 'EditorDialogCtrl',
    controllerAs: '$ctrl',
	  bindToController: true,
		templateUrl: 'aframe/editor/_editor-dialog.html',
    panelClass: 'editor-dialog',
		clickOutsideToClose: true,
		escapeToClose: true,
    focusOnOpen: true,
    zIndex: 85
  });
}

export default {
  name: 'EditorConfig',
  fn: EditorConfig
};

/**
 * This controller is only instantiated with the server-rendered
 * editor component. It works by mounting a number of its
 * behaviours onto the scene component's `SceneCtrl` directly
 * and by proxying the `$aframeScene` service.
 * 
 * @param {Object} $mdPanel      ng-material's panel service
 * @param {Object} $mdDialog     ng-material's dialog service
 * @param {Object} $aframeScene  The scene service
 * @param {Object} $aframeEditor The editor service
 * @param {Object} DATA_MODELS	 Database schema representations
 */
class EditorCtrl {
	constructor($mdPanel, $mdDialog, $aframeScene, $aframeEditor, DATA_MODELS) {
		'ngInject';
		this.$mdPanel = $mdPanel;
		this.$mdDialog = $mdDialog;
		this.$aframeScene = $aframeScene;
		this.$aframeEditor = $aframeEditor;
		/**
		 * Property to store the state of the current scene with regard to
		 * whether there are unsaved changes.
		 * 
		 * @memberof EditorCtrl
		 * @type {Boolean}
		 */
		this._unpublishedChanges = false;
		/**
		 * Editor's reference to what the database's data schema's should look like.
		 * 
		 * @memberof EditorCtrl
		 * @type {Object}
		 */
		this._schemas = DATA_MODELS;
	}

	/**
	 * Utility method for keeping controller's model of scene data in
	 * sync with `$aframeScene`'s.
	 * 
	 * @param  {Object} data Scene data to update with
	 */
	updateSceneData(data) {
		Object.assign(this.SceneCtrl, data);
		this.$aframeScene.scene = data;
		console.info('updated scene with data from editor:', data);
	}

	/**
	 * Proxy method for `$aframeEditor.saveDraft()`.
	 * 
	 * @param  {Boolean}	notify	Whether to inform user of success.
	 */
	saveDraft(notify = true) {
		this.$aframeEditor.saveDraft(notify);
	}

	/**
	 * Proxy method for `$aframeEditor.loadDraft()`.
	 * 
	 * @param  {Object}   draft   Chosen draft from draftList
	 * @param  {Boolean}	notify	Whether to inform user of success.
	 */
	loadDraft(draft, notify = true) {
		this.$aframeEditor.loadDraft(draft._id, notify)
			.then(draftContent => this.updateSceneData(draftContent));
	}

	/**
	 * Calls `$aframeEditor.checkForDraft()` and sets the controller's
	 * `draftList` property to an array of drafts resolved from promise.
	 *
	 * If drafts are found, notifies the user; offers to load the latest draft.
	 * Loads draft if user confirms and updates the scene data accordingly.
	 * 
	 * @param {Boolean}	notify	Whether to inform the user with toast
	 */
	checkForDraft(notify = true) {
		this.draftList = null;
		this.$aframeEditor.checkForDraft(notify)
			.then(drafts => {
				this.draftList = drafts;
				return drafts.length&&notify&&this.$aframeEditor.draftFound(drafts);
			})
			.then(foundDraftContent => foundDraftContent&&this.updateSceneData(foundDraftContent));
	}

	/**
	 * Confirms with user that they would like to discard the draft
	 * in question; calls `$aframeEditor.discardDraft()` if user confirms.
	 * 
	 * @param {Object}  draft   The draft to discard
	 * @param {Boolean}	notify	Whether to inform user with toast
	 */
	discardDraft(draft, notify = true) {
		const confirm = this.$mdDialog.confirm()
      .title('Are you sure?')
      .textContent('This action cannot be undone.')
      .ok('Confirm')
      .cancel('Cancel');
    this.$mdDialog.show(confirm).then(() => {
			this.$aframeEditor.discardDraft(draft._id, notify);
    });
	}

	/**
	 * Calls `$aframeEditor.publish()` directly (proxy method); updates
	 * scene data immediately afterward to keep app and server in sync.
	 * 
	 * @param  {Object|Boolean}  newData Data to publish or false
	 * @param  {Boolean}         notify  Whether to inform user with toast
	 * @return {Promise} 								 Resolves to updated scene data
	 */
	publish(newData, notify = true) {
		return this.$aframeEditor.publish(newData, notify)
			.then(sceneData => this.updateSceneData(sceneData));
	}

	/**
	 * The editor controller's `$onInit` hook takes care of attaching a few
	 * editor methods and properties directly on the scene controller – it's
	 * strategy is to define and intialize all editor behaviour _separate_ from
	 * the scene, so that non-admin users do not have any of its behaviours
	 * attached (or included).
	 *
	 * The things it attaches are:
	 * - A contextmenu listener: since a number of components have different actions
	 * based on right clicks, a general "flag" needs to be set on the scene
	 * controller (`_rightclick`) so components can just check for the flag on any scene click.
	 *   - Also attaches `$onDestroy` for cleanup
	 * - The editor's `checkForDraft()` and `openEditor()` methods
	 * - An `_editable` flag that allows the scene controller to display the UI
	 */
	$onInit() {
		const contextMenu = event => {
		  event.stopPropagation();
			if (!this.SceneCtrl._rightClick) {
				this.SceneCtrl._rightClick = event;
				event.preventDefault();
				this.SceneCtrl.$sceneEl.on('mouseup', () => {
					this.SceneCtrl._rightClick = false;
					this.SceneCtrl.$sceneEl.off('mouseup')
				});
			}
		};
		this.SceneCtrl.$sceneEl.on('contextmenu', contextMenu);
	  this.SceneCtrl.$onDestroy = () => {
			this.$sceneEl.off('contextmenu', contextMenu);
	  }

		this.SceneCtrl.checkForDraft = () => this.checkForDraft();
		this.SceneCtrl.editItem = (ev, item, collection) => this.editItem(ev, item, collection);

		this.SceneCtrl._editable = true;
	}

	/**
	 * Queues up a new item (hotSpot/sceneLink/entity/etc) for addition to the current
	 * scene. Only collections that have their model explicitly defined in `DATA_MODELS`
	 * **and** as an option in the editor 'add item' selection (`_editor.html`) will be
	 * available for use.
	 * 
	 * @param  {Event}   ev         The mouseEvent that called the method
	 * @param  {Array}   collection Collection of scene elements to add item to
	 * @return {Promise} 						Promise representing dialog reference
	 */
	addItem(ev, collection) {
		const locals = { item: this._schemas[collection] };

		locals.newItem = true;
    locals.publish = () => this.$aframeScene.addItemTo(locals.item, this.SceneCtrl[collection], newData => {
			this.$aframeScene.scene[collection] = newData;
			this._panelRef&&this._panelRef.close();
		});
    locals.saveDraft = () => this.$aframeScene.addItemTo(locals.item, this.SceneCtrl[collection], newData => {
			this.$aframeScene.scene[collection] = newData;
			this.saveDraft();
		});
		locals.removeThis = () => this._panelRef&&this._panelRef.close();
		locals.closeDialog = locals.removeThis;

		return this.openEditor(ev, locals);
	}

	/**
	 * Queues up a new scene for creation/publishing. WIP.
	 *
	 * @todo Panorama upload
	 * @todo Go to scene after created
	 * 
	 * @param  {Event} ev The mouseEvent that called the method
	 * @return {Promise}  Promise representing dialog reference
	 */
	newScene(ev) {
		const locals = { item: this._schemas.scene };

		locals.newItem = true;
    locals.publish = () => {
			this.$aframeEditor.publish(locals.item);
			this._panelRef&&this._panelRef.close();
		};
    locals.saveDraft = () => {
    	locals.item.state = 'draft';
    	locals.publish();
		};
		locals.removeThis = () => this._panelRef&&this._panelRef.close();
		locals.closeDialog = locals.removeThis;

		return this.openEditor(ev, locals);
	}

	/**
	 * Opens a generic context menu 'edit' dialog for scene elements. Mostly
	 * proxies this controller's method's directly inside the editor dialog.
	 * Includes a horizontal offset that approximates the width of most average
	 * scene elements to center the dialog's origin.
	 * 	
	 * @param  {Event} ev         				The mouseEvent that called the method
	 * @param  {Object} item       				The item to edit
	 * @param  {Array<Object>} collection The item's collection in the scene
	 * @return {Promise}  								Promise representing dialog reference
	 */
	editItem(ev, item, collection) {

		const locals = { item };
    locals.publish = () => this.publish();
    locals.saveDraft = () => this.saveDraft();
		locals.removeThis = () => this.$aframeScene.removeItemFrom(item, collection, () => {
			this._panelRef&&this._panelRef.close();
		});
		locals.closeDialog = () => this._panelRef&&this._panelRef.close();

		return this.openEditor(ev, locals, { offsetY: -250 });
	}

	/**
	 * Base method for layering other dialog types into. This method
	 * handles the actual initialization of the editor dialog component
	 * using the ng-material panel service's `$mdPanel.open()` method on
	 * a panel preset built in `editor_config.js`.
	 *
	 * If the item loaded into the editor is considered "new", the dialog
	 * is readied for the center of the screen; otherwise, it is placed
	 * in the bottom-left. The animate-to-and-from is calculated using
	 * the original MouseEvent that opened the dialog.
	 *
	 * An `onDomRemoved()` method is added to clean up the dialog scope
	 * when its elements are removed; the scope is provided by a promise
	 * returned by this method.
	 * 
	 * @param  {Event} ev              	The mouseEvent that called the method
	 * @param  {Object} locals          Local values to pass into dialog
	 * @param  {Number} $2.offsetX 			Pixels to shift dialog along X
	 * @param  {Number} $2.offsetY 			Pixels to shift dialog along Y
	 * @return {Promise}  							Promise representing dialog scope
	 */
	openEditor(ev, locals, { offsetX = 0, offsetY = 0 } = {}) {
		console.log((ev.clientY + offsetY))
		const position = this.$mdPanel.newPanelPosition();
		if (locals.newItem) {
			position.absolute().center();
		} else {
			position.absolute().bottom(0).left(0);
		}
    const animation = this.$mdPanel.newPanelAnimation()
      .openFrom({top: (ev.clientY + offsetY), left:( ev.clientX + offsetX) })
      .withAnimation(this.$mdPanel.animation.SCALE)
      .closeTo({top: (ev.clientY + offsetY), left:( ev.clientX + offsetX) });
    const onDomRemoved = () => {
			this._panelRef&&this._panelRef.destroy();
		};

		return this.$mdPanel.open('editorDialog', {
			position, animation, onDomRemoved, locals
		}).then(scope => {
      this._panelRef = scope;
    });
	}
}

export default EditorCtrl;

import { pick } from 'lodash';
/**
 * Service for handling all CRUD operations pertaining to scenes.
 *
 * Contains methods for drafting and publishing; interfaces directly
 * with the `$aframeScene` service for a reference to the current
 * scene object. Uses:
 * 
 * - ng-material's `$mdToast` service to display UI messages to user
 * - the `$tourApi`'s scene resource and the `DraftResource` for CRUD ops
 * - the 'EDITOR_MESSAGES' constant to provide prebuilt dialogs and toasts
 * 
 * @param  {object} $mdToast        ng-material's toast service
 * @param  {object} $tourApi        Tour $resource factory service
 * @param  {object} DraftResource   Draft $resource factory
 * @param  {object} EDITOR_MESSAGES Constant to define toast configs
 */
class $aframeEditor {
	constructor($mdToast, $aframeScene, $tourApi, DraftResource, EDITOR_MESSAGES) {
		'ngInject';
		this.$mdToast = $mdToast;
		this.$aframeScene = $aframeScene;
		this.DraftResource = DraftResource;
		this.SceneResource = $tourApi.scene;
		/**
		 * Property to hold an array of fully initialized toast config objects; loops
		 * through `EDITOR_MESSAGES` and sets properties using the methods provided
		 * by `$mdToast.simple()`'s return.
		 *
		 * @memberof $aframeEditor
		 * @type {Object}
		 */
		this.toasts = {};
		Object.keys( EDITOR_MESSAGES ).forEach(type => {
			const toast = $mdToast.simple();
			const toastConfig = EDITOR_MESSAGES[type];
			Object.keys(toastConfig).forEach(prop => {
				toast[prop]( toastConfig[prop] );
			});
			this.toasts[type] = toast;
		});
	}

	/**
	 * Sends an HTTP `POST` or `PATCH` request via the `$tourApi`'s
	 * scene resource to publish (save) the current scene.
	 *
	 * If the scene is new, the first parameter of this method can be
	 * passed the new scene data, which makes it implicity truthy and
	 * tells the method to send the request as a new scene creation. If
	 * left to default, it will stay false and the method will assume
	 * the scene already exists and is being updated.
	 * 
	 * In the case of an update, Lodash's `pick()` method is used to grab
	 * applicable properties from the current `$aframeScene.scene` _without_
	 * creating ones that don't exist. A new `updatedAt` property is assigned
	 * a date for every action; if the data was new, the same date is given
	 * to a `createdAt` property.
	 * 
	 * @param  {Boolean}  _newData	Whether incoming data should be treated as new
	 * @param  {Boolean}  notify 		Whether to notify the user (toast)
	 * @return {Promise} 						Resolves to latest scene data
	 */
	publish(_newData = false, notify = true) {
		let action = 'update';
		const newData = _newData || pick(this.$aframeScene.scene, [
			'_id',
			'code',
			'name',
			'assets',
			'entities',
			'hotSpots',
			'sceneLinks',
			'panorama',
			'parent',
			'script'
		]);
		newData.updatedAt = new Date();
		if (_newData) {
			newData.createdAt = newData.updatedAt;
			action = 'save';
		}

  	return this.SceneResource[action](_newData ? null : { 
  		id: newData._id
  	}, newData)
  	.$promise.then(scene => {
			this.lastPublished = this.$aframeScene.scene || scene;
  		notify&&this.$mdToast.show(this.toasts.publish);
  		return scene;
  	});
	}

	/**
	 * Sends an HTTP `POST` request to save a draft of the current scene.
	 * Offers to publish the scene when the draft is done saving; calls
	 * `publish()` if the user confirms.
	 * 
	 * @param  {Boolean} notify 	Whether to notify the user (toast)
	 * @return {Promise} 					Resolves to saved draft
	 */
	saveDraft(notify = true) {
  	return this.DraftResource.save({
  		content: this.$aframeScene.scene,
  		kind: 'Scene',
  		original: this.$aframeScene.scene._id
  	}).$promise.then(draft => {
	  	this.lastDraft = draft.content;
	    notify&&this.$mdToast.show(this.toasts.saveDraft).then(response => {
	      if ( response == 'ok' ) {
	      	this.publish();
	      }
	    });
	    return draft;
  	});
  }

	/**
	 * If there is scene data available, queries the database by the current
	 * scene ID via the `DraftResource` factory to check for active drafts
	 * held by the current logged in user.
	 * 
	 * @param  {Boolean}  notify 		Whether to notify the user (toast)
	 * @return {Promise}            Promise that will resolve to draft list
	 */
	checkForDraft(notify = true) {
		return (this.$aframeScene.scene)&&this.DraftResource.query({
			sort: '-updatedAt',
			filter: {
				original: this.$aframeScene.scene._id
			}
		}).$promise;
	}

	/**
	 * Informs a user that draft[s] have been found for the scene
	 * they are viewing and offers to load the most recent draft; calls
	 * `loadDraft()` if the user confirms and supplies it the `_id` of
	 * the first draft in the found draft list.
	 * 
	 * @param  {Array}   drafts A list of available drafts
	 * @return {Promise}      	Resolves to toast result » loadDraft promise
	 */
	draftFound(drafts) {
		return this.$mdToast.show(this.toasts.draftFound)
			.then( response => (response === 'ok') && this.loadDraft(drafts[0]._id) );
	}

	/**
	 * Gets a single stored draft by its database `_id` and passes
	 * the result to a callback; notifies user that draft was loaded and
	 * offers to publish.
	 * 
	 * @param  {string}   id        The id of the draft to fetch
	 * @param  {Boolean}  notify 		Whether to notify the user (toast)
	 * @return {Promise} 					  Resolves to content of the loaded draft
	 */
	loadDraft(id, notify = true) {
  	return this.DraftResource.get({ id }).$promise.then(draft => {
	  	this.lastDraft = draft.content;
	    notify && this.$mdToast.show(this.toasts.draftLoaded)
		    .then( response => ( response === 'ok' ) && this.publish() );
	    return draft.content;
  	});
	}

	/**
	 * Immediately load last saved draft of current scene.
	 * @param  {Boolean}  notify 		Whether to notify the user (toast)
	 * @return {Promise} 					  Resolves to state of confirm toast
	 */
	revertToDraft(notify = true) {
		return this.checkForDraft(false)
			.then( drafts => this.loadDraft(drafts[0]._id, false) )
	  	.then( draftContent => notify && this.$mdToast.show(this.toasts.revertToDraft) );	
	}

	/**
	 * Sends an HTTP `DELETE` request to remove a stored draft by
	 * its database `_id`; notifies user if successful.
	 * 
	 * @param  {string}   id        The id of the draft to delete
	 * @param  {Boolean}  notify 		Whether to notify the user (toast)
	 * @return {Promise} 					  Resolves to state of confirm toast
	 */
	discardDraft(id, notify = true) {
  	return this.DraftResource.remove({ id }).$promise
	  	.then( discardedDraft => notify && this.$mdToast.show(this.toasts.discardDraft) );
	}
}

export default $aframeEditor;

class EntityCtrls {
  // $injector annotations
  static get $inject() {
    return ['$scope'];
  }
  ////////
  constructor($scope) {
    this.$scope = $scope;
  }
  ////////
  $onInit() {
    this.$ngModel.$formatters.push(modelValue => {
      let { type, attrs } = modelValue;
      console.info('control $formatter', { type, attrs });
      return {
          type,
          attrs
      };
    });
    ////
    this.$ngModel.$render = () => {
      const { type, attrs } = this.$ngModel.$viewValue;
      console.log('control $render', { type, attrs });
      this.$scope.type = type;
      this.$scope.attrs = attrs;
    };
    ////
    this.$scope.$watch(
      () => { 
        return {
          attrs: this.$scope.attrs, type: this.$scope.type 
        }
      },
      (newVal) => {
        console.log('control $watch', newVal);
        this.$ngModel.$setViewValue({
          type: this.$scope.type,
          attrs: this.$scope.attrs
        });
      }, true
    );
    ////
    this.$ngModel.$parsers.push(viewValue => {
      let { type, attrs } = viewValue;
      console.log('control $parser', { type, attrs });
      return { type, attrs };
    });
  }
  ////////
  addAttr() {
    console.log('attr added from control');
    return this.$scope.attrs.push({prop:'', val:''});
  }
  ////////
  removeAttr(attr) {
    angular.forEach(this.$scope.attrs, (a, i) => {
      if (a.$$hashKey === attr.$$hashKey) {
        console.log('attr removed from control', a);
        return this.$scope.attrs.splice(i, 1);
      };
    });
  }
}

const entityControls = {
