function sceneLink($state, $popupWindow, $tourApi, $analytics) {
  'ngInject';
  return {
    restrict: 'A',
    scope: {
    	sceneLink: '='
    },
    require: '^aframeScene',
    templateUrl: 'aframe/scenelink/_scenelink.html',
		link(scope, elem, attrs, SceneCtrl) {
			const SceneResource = $tourApi.scene,
						LocationResource = $tourApi.location;
			const sceneId = scope.sceneLink.scene;
			//  and handlers: success...
			const gotoSceneHandler = data => {
				$analytics.eventTrack('click', { category: 'scenelink', label: [data.scene.parent.code, data.scene.code].join('_') });
				$state.transitionTo('scene', { location: data.location.code, building: data.scene.parent.code, scene: data.scene.code });
				// $state.go('scene', { building: data.parent.code, scene: data.code });
			}
			// ...and error
			const errorHandler = error => {
			  const locals = {
					type: 'Scene load error!',
					message: 'Looks like this scene link is broken – darn.'
		  	}
				$popupWindow.error({ locals });
			}
			//
			// Listen for clicks on scene link element
			scope.elemClick =  () => {
				// If there's a right click active...
				if (SceneCtrl._rightClick && SceneCtrl._editable) {
					// ...open the scene link editor:
					SceneCtrl.editItem(SceneCtrl._rightClick, scope.sceneLink, SceneCtrl.sceneLinks);
				} else {
					// ...otherwise just use the default scene link behavior
					document.getElementById(`link_${sceneId}`).emit('goto');
					let scene;
					SceneResource.get({ id: sceneId }).$promise
						.then(data => {
							scene = data;
							return LocationResource.get({ id: scene.parent.parent }).$promise;
						}) //**
						.then(location => ({ location, scene }) ) //**
						.then(gotoSceneHandler, errorHandler);
				}
			};
			// elem.on('click', elemClick);
    }
  };
}

export default {
  name: 'sceneLink',
  fn: sceneLink
};

const aframeSky = {
  bindings: {
    sky: '<',
    preload: '<?'
  },
  require: {
  	SceneCtrl: '^aframeScene'
  },
  controller: 'SkyCtrl'
};

export default {
  name: 'aframeSky',
  fn: aframeSky
};

/**
 * The `SkyCtrl` class is largely responsible for
 * orchestrating the translation of URLs into IDs
 * and then into DOM elements for attaching to the scene.
 *
 * It attaches a single `<a-sky/>` the first time a sky is
 * loaded, and attaches `<img/>`s to the scenes assets
 * preload bucket based on the `$aframeSky` service's instructions.
 * 
 * @memberOf app.components.aframe.sky
 */
class SkyCtrl {
	/**
	 * Initializes the controller's dependencies:
	 * - Binds a special one-off $compile function for when
	 * 	 the scene's `<a-sky />` needs to be compiled
	 * - Attaches the $aframeSky service directly
	 * 
	 * @param  {object} $scope     The SkyCtrl's scope
	 * @param  {object} $compile   Angular's compile service
	 * @param  {object} $aframeSky The sky component's biz logic
	 */
	constructor($scope, $compile, $aframeSky) {
		'ngInject';
		this.compileSkyEl = callback => $compile('<a-sky ng-src="{{ \'#\' + $ctrl.loadedSky }}" />')($scope, callback);
		this.$aframeSky = $aframeSky;
	}

  /**
	 * Store JQLite-wrapped `<a-scene>` and `<a-assets>`;
	 * init internal check for right click event.
   * 
   * @return {void}		No return
   */
  $onInit() {
		/**
		 * Cache vars to store JQLite <img> and <a-sky>
		 * Flag for init sky load and array of loaded skies
		 * 
		 * @type {null}
		 * @type {null}
		 * @type {boolean}
		 * @type {Array}
		 */
		this.$sceneEl = this.SceneCtrl.$sceneEl;
		this.$assetsEl = this.SceneCtrl.$assetsEl;
		this._skyElLoaded = false;
		this._skyLoadedList = this.$aframeSky.CircularBuffer(10);
  }

  /**
   * Lifecycle hook that is invoked every time `SkyCtrl.sky`
   * changes value. The first change is discarded since it will
   * always return `null` or `undefined`.
   * 
   * @param  {object} newData Incoming change object
   */
  $onChanges(newData) {
  	if (newData.sky.isFirstChange()) return;
  	let skyUrl = newData.sky.currentValue;
  	if (skyUrl) this.setSky(skyUrl, this.assetIdFromSkyUrl(skyUrl));
  }

  /**
   * Utility function for stripping the `public_id`
   * property out of a URL segment in the form of
   * `[version]/[path/to/image]/[public_id]`.
   * 
   * @param  {string} skyUrl Panorama URL segment
   * @return {string}        Panorama's public_id
   */
	assetIdFromSkyUrl(skyUrl) {
		return skyUrl.split('scenes/panorama/')[1].split('.')[0]
	}

	/**
	 * Uses the $aframeSky service to fetch a server-generated
	 * list of `skyUrl`s for asset preloading. It grabs the `_id`
	 * of the current active scene from `SceneCtrl` and sends it
	 * through `$aframeSky.getPreloadList()` to generate the list
	 * based on scenelinks in the current active scene.
	 *
	 * `assetIdFromSkyUrl()` turns each `skyUrl` into an `assetId`,
	 * which is checked against the list of preloaded assets.
	 * If the asset hasn't been loaded and stored already, 'loadSkyAsset()'
	 * queues it up for loading into the scene's `<a-assets></a-assets>`.
	 * 
	 * @return {Promise} Promise that holds an array of skyUrls from server
	 */
	preload() {
		this.$aframeSky.getPreloadList(this.SceneCtrl._currentSceneId)
			.then(toPreload => {
				toPreload.forEach(skyUrl => {
					const newAssetId = this.assetIdFromSkyUrl(skyUrl)
					if (!this._skyLoadedList.toArray().includes(newAssetId)) {
						this.loadSkyAsset(skyUrl, newAssetId);
					}
				});
			})
			.catch(err => {
				console.warn(err);
			});
	}

	/**
	 * Sets the current active sky to an `assetId`. This updates the
	 * binding in the scene's `<a-sky ng-src="{{ $ctrl.loadedSky }}" />`
	 * to the provided `assetId`, which should match the `id=""` of a
	 * asset that has already been fully loaded since this represents
	 * the largest and final change a user perceives when switching scenes.
	 *
	 * If this function is being called, it is safe to assume all
	 * requisite loading steps for scene transition have completed, so
	 * it is safe to `preload()` the next wave of assets.
	 * 
	 * @param {string} assetId public_id of panorama that is done loading
	 */
	setSkyId(assetId) {
		this.loadedSky = assetId;
		this.preload();
	}

	/**
	 * Acts as a buffer function to `setSkyId()` by checking if the
	 * incoming assetId is already in the list of loaded skies –
	 * if the list includes the ID, `setSkyId()` is called on the ID
	 * directly. If the ID is missing from the list, `loadSkyAsset()`
	 * is initiated.
	 * 
	 * `setSkyId()` is called after the promise is resolved inside its
	 * `then()` method, where a check is also performed whether the
	 * scene needs an `<a-sky />` element.
	 * 
	 * @param {string} skyUrl  Panorama URL segment
	 * @param {string} assetId Panorama's public_id
	 */
	setSky(skyUrl, assetId) {
    return this._skyLoadedList.toArray().includes(assetId) ?
      this.setSkyId(assetId) :
      this.loadSkyAsset(skyUrl, assetId)
      	.then(assetId => {
	        if(!this._skyElLoaded) {
	          this.$skyEl = this.loadSkyEl();
	        }
	        this.setSkyId(assetId)
	      });
	}

	/**
	 * Compiles a new image asset with requested URL and ID;
	 * adds element to assets and ID to list to prevent reload.
	 * 
	 * @param {string} skyUrl  Panorama URL segment
	 * @param {string} assetId Panorama's public_id
	 * @return {string}        The original assetId
	 */
	loadSkyAsset(skyUrl, assetId) {
		return this.$aframeSky.getSkyDomNode(skyUrl, assetId)
			.then(imgNode => {
				this.$assetsEl.append(imgNode);
				this._skyLoadedList.push(assetId);
				console.log('sky asset preloaded: ', skyUrl)
				return assetId;
			})
	}

	/**
	 * Runs the `compileSkyEl()` method from the constructor
	 * to build the sky element; attaches it to the scene
	 * and marks the element as loaded.
	 * 
	 * @param  {string} sky ID for asset reference
	 * @return {Element}    JQLite-wrapped <a-sky> element
	 */
	loadSkyEl() {
		return this.compileSkyEl(clone => {
			this.$sceneEl.append(clone);
			this._skyElLoaded = true;
			return clone;
		});
	}
}

export default {
  name: 'SkyCtrl',
  fn: SkyCtrl
};

import { utils } from 'aframe';

const COMPRESS_MAX = 100, COMPRESS_FACTOR = 12;

/**
 * The $aframeSky class is a service responsible for
 * the business logic of the aframeSky component.
 *
 * The SceneCtrl controller uses the `$aframeSky.sky`
 * getter/setter to detect changes to the active scene's
 * sky; the SkyCtrl controller uses the `getSkyDomNode()`
 * method to produce a fully loaded `<img>` for appending
 * the SceneCtrl.$assetsEl (`<a-assets>`).
 * 
 * @memberOf app.components.aframe.sky
 */
class $aframeSky {
	constructor($q, $tourApi, CBuffer, UserSession, GLOBAL_SETTINGS) {
		'ngInject';
		this.$q = $q;
		this.Preload = $tourApi.preload;
		this.CircularBuffer = CBuffer;
		this.UserSession = UserSession;

		this.imageApiUrl = GLOBAL_SETTINGS.imageApiUrl;
	}
	/**
	 * Getter/setter for formatting sky URL from sceneData.
	 * 
	 * @type {string}
	 * @example
	 * 'http://res.cloudinary.com/uoit-virtual-tour/image/upload/v1480529929/scenes/panorama/k23dnerigdctblxgq5vm.jpg'
	 */
	get sky() {
		return this.panorama ? [ this.panorama.version, this.panorama.public_id].join('/') : null;
	}
	set sky(panorama) {
		console.log('sky service set to:', panorama)
		this.panorama = panorama;
	}

	/**
	 * Utility function for reading and parsing user settings
	 * based on aframe's isMobile() method and the UserSession
	 * service's usage settings; parses into an object that
	 * eventually becomes a Cloudinary URL component string.
	 * 
	 * If the device is mobile, limit image resolution to 2048x1024
	 * due to rendering limit.
	 * 
	 * @return {string} Formatted Cloudinary setting string (`c_scale,f_auto,etc`)
	 */
	getSettings() {
		const mobile = (utils.isMobile || utils.device.isMobile)(),
					lowRes = !this.UserSession.usage.resolution.val;
		const settings = {
			w: mobile||lowRes ? 2048 : 4096,
			h: mobile||lowRes ? 1024 : 2048,
			c: 'scale',
			f: 'auto',
			q: COMPRESS_MAX - (COMPRESS_FACTOR * this.UserSession.usage.compression.val)
		}
		const output = [];
		Object.keys(settings).forEach(key => {
			output.push(`${key}_${settings[key]}`)
		})
		return output.join(',');
	}

	/**
	 * Fetches an efficiently-generated list of `skyUrl`s
	 * from the server, which uses a scene `_id` to read
	 * potential candidates for preloading from the scene's
	 * scenelinks.
	 *
	 * If the `UserSession` service's preloading settings are
	 * set to 1 or 2, it returns the full list for further
	 * manipulation; otherwise, it returns an empty array
	 * (0/no preload).
	 * 
	 * @param  {string} id The _id of a scene to preload links for
	 * @return {Promise}   A promise that will fulfill the preload list
	 */
	getPreloadList(id) {
		return this.$q((resolve, reject) => {
			switch(this.UserSession.usage.preloading.val) {
				case 0:
					resolve([]);
					break;
				case 1:
				case 2:
					this.Preload.get({ id }).$promise.then(data => resolve(data));
					break;
				default: 
					reject('Can\'t determine preload settings')
			}
		});
	}

	/**
	 * Converts a skyUrl and an assetId into a fully
	 * compiled DOM element; returns a promise representing
	 * a sky asset that will eventually be fully loaded.
	 *
	 * Uses `getSettings()` to parse userSession settings
	 * into a Cloudinary setting string.
	 * 
	 * @param  {string} url Partial URL, e.g. `this.sky` getter/setter
	 * @param  {string} id  An asset ID, e.g. the panorama's `public_id`
	 * @return {Promise}    A promise that will resolve to the loaded `<img>`
	 */
	getSkyDomNode(url, id) {
	  return this.$q((resolve, reject) => {
			const img = angular.element(`<img src="${ this.imageApiUrl }/${ this.getSettings() }/v${ url }" id="${ id }" crossOrigin="anonymous" />`);
	    img.on('load', () => resolve(img));
	    img.on('error', () => reject('Image load error'));
	  });
	}
}

export default {
  name: '$aframeSky',
  fn: $aframeSky
};

