import { lowercase } from 'angular';

class SearchCtrl {
	constructor($timeout, $log, $http, SEARCH_FILTERS) {
		'ngInject';
		this.$timeout = $timeout;
		this.$log = $log;
		this.$http = $http;

		const { typeFilters, modelFilters, fieldFilters } = SEARCH_FILTERS;
		Object.assign(this, { typeFilters, modelFilters, fieldFilters });
	}

	$onInit() {
		this.searchResults = [];
		this.filters = {
			in: [],
			for: [],
			by: []
		};
	}
	querySearch (q) {
		if (!q) return [];
		const params = { q };
		if (this.filters.for.length) params.filter = this.filters.for;
		if (this.filters.by.length) params.fields = this.filters.by;
		console.log(params);
		return this.$http.get('/api/v1/search', { params }).then(result => {
			const output = [];
			Object.keys(result.data.results).forEach(type => output.push(...result.data.results[type]));
			this.searchResults = output;
			// return output;
			return result.data.overview;
		});
	}

	searchTextChange(text) {
		this.$log.info('Text changed to ' + text);
	}

	selectedItemChange(item) {
		this.$log.info('Item changed to ' + JSON.stringify(item));
	}
}

export default {
  name: 'SearchCtrl',
  fn: SearchCtrl
};

import { isNumber } from 'angular';

/**
 * Upper limit of "high-speed" connection rating (in Mbps).
 * @memberof ConnectionDetails
 * @type {Number}
 */
const SPEED_HI = 50;

/**
 * Upper limit of "wi-fi" connection rating (in Mbps).
 * @memberof ConnectionDetails
 * @type {Number}
 */
const SPEED_WF = 8;

/**
 * Upper limit of "mobile/3G" connection rating (in Mbps).
 * @memberof ConnectionDetails
 * @type {Number}
 */
const SPEED_3G = 2;

/**
 * This service is responsible for translating user network and device
 * details into a usable format for application settings.
 *
 * It contains methods for detecting details, optimizing settings based
 * on details, calculating general reports of setting levels, and
 * providing human-readable versions of those reports.
 * 
 * @param  {Object} $rootScope    The root of the app scopes
 * @param  {Object} $http         Angular's http service
 * @param  {Object} $q            Angular's promise builder
 * @param  {Object} USER_DEFAULTS Constant for default settings
 */
class ConnectionDetails {
	/**
	 * Initializes the service's dependencies. Extracts usage profiles
	 * from the `USER_DEFAULTS.profiles` constant and discards other settings.
	 */
	constructor($rootScope, $http, $q, USER_DEFAULTS) {
	  'ngInject';
	  this.$rootScope = $rootScope;
		this.$http = $http;
		this.$q = $q;

		this.profiles = USER_DEFAULTS.profiles;
	}

	/**
	 * Uses Angular's `$http` service to send a request to the server
	 * for a connection test. Internally, the server API measures
	 * connection speed in the background, and supplies this info back
	 * to the app along with device details when it becomes available.
	 * 
	 * @return {Promise} Promise that will resolve to connection details
	 */
	detect() {
	  return this.$http.get('/user/connection').then(connection => {
	  	this.connection = connection;
	  	return connection;
	  });
	}

	/**
	 * Determines the best usage profile (from `USER_DEFAULTS.profiles`)
	 * to select based on a connection details object (e.g. the result of
	 * calling `detect()`).
	 * @param  {Object} connection Incoming connection details object
	 * @return {Object}            The chosen optimal settings profile
	 */
	optimize(connection) {
		const { network, useragent } = connection.data,
					downloadSpeed = network.speeds.download,
					deviceType = useragent.device.type;

		if (downloadSpeed >= SPEED_HI && !deviceType)
			return this.profiles.desktopFast;
		if (downloadSpeed >= SPEED_WF && downloadSpeed < SPEED_HI && !deviceType)
			return this.profiles.desktopSlow;
		if (downloadSpeed < SPEED_WF && !deviceType)
			return this.profiles.balanced;
		if (downloadSpeed >= SPEED_WF && deviceType  === 'mobile')
			return this.profiles.mobileWifi;
		if (downloadSpeed >= SPEED_3G && deviceType  === 'mobile')
			return this.profiles.mobile3g;
		if (downloadSpeed < SPEED_3G)
			return this.profiles.conserve;
		// default return
		return this.profiles.balanced;
	}

	/**
	 * Tallies up values of usage settings asynchronously
	 * by using `$scope.$eval()` to match applicable usage
	 * level values against their keys (which are keyed by
	 * "condition").
	 * @example
	 * {
	 *   '<= 15': [0, 0, 5],
	 *   '== 0': [...]
	 * }
	 * @param	 {Object}	usage Incoming usage preference object
	 * @return {Promise}			Promise representing final levels
	 */
	calculateUsageLevel(usage) {
		const tally = [0, 0, 0], // [imageQual, loadTime, dataUse] / 0=low, 10=high
	  			deferred = this.$q.defer(),
	  			addToTally = values => {
						tally.forEach((v, index) => {
							tally[index] += values[index];
						});
					};
		this.$rootScope.$applyAsync(() => {
			Object.keys(usage).forEach(setting => {
				if (setting !== 'auto') {
					Object.keys(usage[setting].levels).forEach(expression => {
						const inRange = this.$rootScope.$eval(`(${ usage[setting].val } ${expression})`);
						if (inRange) addToTally(usage[setting].levels[expression]);
						if (isNumber(inRange)) deferred.reject('Usage calculation error');
					});
				}
			});
			deferred.resolve(tally);
		});
		return deferred.promise;
	}
	// TODO(PERF): CLEAN UP THIS FUNCTION; TOO MANY NESTED CLOSURES

	/**
	 * Maps a tally array of numbers into a label
	 * array of human-readable usage level strings.
	 * @param  {Array<Number>}  tally Array of tallied usage values
	 * @return {Array<String>}       	Usage descriptions
	 */
	getLabelsFromTally(tally) {
		return tally.map(value => {
			if (value <= 5) return 'very low';
			if (value > 5 && value <= 10) return 'low';
			if (value > 10 && value <= 15) return 'moderate';
			if (value > 15 && value <= 20) return 'high';
			if (value > 20) return 'very high';
		});
	}
}

export default {
  name: 'ConnectionDetails',
  fn: ConnectionDetails
};

import { element } from 'angular';

/**
 * The settings controller provides a high-level interface to
 * the app's user preferences and data usage settings, login/logout
 * functions, and a set of automatic usage detection functions.
 * 
 * @param {object} $scope 					 The current scope
 * @param {object} $animate          For animating backdrop only
 * @param {object} $mdUtil           Init backdrop utility
 * @param {object} $popupWindow      Supplies login window
 * @param {object} UserAuth          Authentication check
 * @param {object} UserSession       Holds all settings
 * @param {object} ConnectionDetails Detects network and device
 * @param {object} AUTH_EVENTS       Authorization event names
 */
class SettingsCtrl {
	constructor($scope, $animate, $mdUtil, $popupWindow,
	UserAuth, UserSession, ConnectionDetails,
	AUTH_EVENTS) {
		'ngInject';
		this.$scope = $scope;
		this.$animate = $animate;
		this.$mdUtil = $mdUtil;
		this.$popupWindow = $popupWindow;
		this.UserAuth = UserAuth;
		this.UserSession = UserSession;
		this.ConnectionDetails = ConnectionDetails;
		this.AUTH_EVENTS = AUTH_EVENTS;
	}
	/**
	 * Lifecycle hook to initialize dependencies when component
	 * is mounted.
	 * 
	 * Stores authentication and user preference detail objects,
	 * inits connection object to hold network info. Sets up
	 * one-time `$watch` binding to listen for accordion init
	 * since its contents load async before it can open.
	 *
	 * Sets up `$on('event')` listener for login success; passes
	 * user data from event into controller and syncs applicable
	 * view elements; opens accordion to expose settings to user.
	 */
	$onInit() {
		this.isLoggedIn = this.UserAuth.isAuthenticated;
		this.user = this.UserSession.user;
		this.settings = this.UserSession.settings;
		this.usage = this.UserSession.usage;

		/**
		 * This property will hold the user's network and
		 * device information when it becomes available; it
		 * remains false until availability.
		 *
		 * @memberof SettingsCtrl
		 * @type {boolean|object}
		 * @example
		 * {
		 *   "network": {
		 *     "speeds": { ... },
		 *     ...
		 *   },
		 *   "useragent": {
		 *     "device": { ... },
		 *     ...
		 *   }
		 * }
		 */
		this.connection = false;

		/**
		 * The accordion initializer watches for a valid
		 * instance and opens the accordion once it becomes
		 * available. Performed once then deregistered.
		 * 
		 * @memberof SettingsCtrl
		 */
		const accordionReady = this.$scope.$watch('accordion', accordion => {
			if (accordion) {
				this.$scope.$applyAsync(() => {
					this.isLoggedIn() && accordion.expandAll();
				});
				accordionReady();
			}
		});

		this.$scope.$on(this.AUTH_EVENTS.loginSuccess, this.loadUserAfterLogin.bind(this) );

		this.getUsageLevel();
	}

	/**
	 * Expands all accordion sections.
	 */
	expandAll() {
		this.$scope.accordion.expandAll();
	}
	/**
	 * Collapses all accordion sections.
	 */
	collapseAll() {
		this.$scope.accordion.collapseAll();
	}

	/**
	 * Prompts user for login using `$popupWindow` service's `login()` dialog.
	 * @return {Promise} Status of popupWindow
	 */
	promptLogin() {
		return this.$popupWindow.login();
	}
	/**
	 * Logs user out directly with `UserAuth.logout()`.
	 */
	logout() {
		const backdrop = this.$mdUtil.createBackdrop(this.$scope, "md-dialog-backdrop md-opaque");
		backdrop[0].tabIndex = -1;
		this.$animate.enter(backdrop, element(document.body), null);
		this.UserAuth.logout().then(() => {
			this.$animate.leave(backdrop);
			this.collapseAll();
		});
	}

	/**
	 * Stores user info and opens accordion sections when login is detected.
	 * 
	 * @param  {Event} event  The originating event object
	 * @param  {Object} user  The newly logged-in user
	 */
	loadUserAfterLogin(event, user) {
		this.user = this.UserSession.user;
		this.settings = this.UserSession.settings;
		this.usage = this.UserSession.usage;
		this.updateUsage();
		this.updateSettings();
		this.expandAll();
	}

	/**
	 * Updates the user's setting preferences.
	 */
	updateSettings() {
		this.UserSession.settings = this.settings;
	}
	/**
	 * Updates the user's usage preferences and usage level description text.
	 * 
	 * @param  {[object]} usage Optional data override
	 */
	updateUsage(usage) {
		this.UserSession.usage = usage || this.usage;
		this.getUsageLevel();
	}
	/**
	 * Uses the `ConnectionDetails` service to convert the current
	 * session's usage preferences into a user-friendly description
	 * of what the preferences' effects will be.
	 * - Grabs `UserSession.usage` details
	 * - Uses `ConnectionDetails.calculateUsageLevel()` to tally usage stats from details
	 * - Passes resulting tally to `ConnectionDetails.getLabelsFromTally()`
	 * - Assigns resulting descriptions to controller
	 */
	getUsageLevel() {
		this.ConnectionDetails.calculateUsageLevel(this.UserSession.usage).then(usageLevelTally => {
			this.usageLevel = this.ConnectionDetails.getLabelsFromTally(usageLevelTally);
		});
	}

	/**
	 * User-activated function for auto-optimizing usage
	 * level using the `ConnectionDetails` service.
	 * - Sets loading animation during detection
	 * - Fetches connection information with `detect()`
	 * - Stores returned connection information
	 * - Sets optimized usage settings with `optimize()`
	 */
	detectConnection() {
		if (this.usage.auto.val) {
			this.connection = { loading: true };
			this.ConnectionDetails.detect().then(connection => {
				this.connection = connection;
				this.usage = this.ConnectionDetails.optimize(connection);
				this.updateUsage();
			});
		}
	}
}

export default {
	name: 'SettingsCtrl',
	fn: SettingsCtrl
};

