angular.module('app', [
  uiRouter,
  ngAnimate,
	ngAria,
  ngMaterial,
  ngMdIcons,
  cfpLoadingBar,
  vAccordion,
  Common,
  Components
])

.component('app', AppComponent)

.config(($provide, $locationProvider, $stateProvider, $urlRouterProvider, $mdThemingProvider, cfpLoadingBarProvider) => { //, $mdIconProvider
  'ngInject';

  // cfpLoadingBarProvider.includeSpinner = false;
  // cfpLoadingBarProvider.latencyThreshold = 300;
  cfpLoadingBarProvider.spinnerTemplate = `<div id="loading-spinner" class="loading-spinner-container" layout layout-fill layout-align="center center">
  	<md-progress-circular md-mode="indeterminate" class="md-primary">
  	</md-progress-circular>
  </div>`;

  $mdThemingProvider.definePalette('UOITprimary', {
    '50': '0086FC',
    '100': '45A1FA',
    '200': '74B8FB',
    '300': 'A2D0FD',
    '400': '0077CA',
    '500': '006BB8',
    '600': '005FA6',
    '700': '005495',
    '800': '004883',
    '900': '003C71',
    'A100': 'ff8a80',
    'A200': 'ff5252',
    'A400': 'ff1744',
    'A700': 'd50000',
    'contrastDefaultColor': 'light',    // whether, by default, text (contrast)
                                        // on this palette should be dark or light
    'contrastDarkColors': ['50', '100', //hues which contrast should be 'dark' by default
     '200', 'A100'],
    'contrastLightColors': null    // could also specify this if default was 'dark'
  });

  $mdThemingProvider.theme('default')
    .primaryPalette('UOITprimary', {
      'default': '400',
      'hue-1': '300',
      'hue-2': '500',
      'hue-3': '900'
    })
    .accentPalette('grey');

  $locationProvider.html5Mode(true);

  $stateProvider
    .state('home', {
      template: '<ui-view></ui-view>'
    });

  $urlRouterProvider.otherwise('/');

	$provide.decorator('$exceptionHandler', ($log, $delegate, $injector) => {
		'ngInject';
    return (exception, cause) => {
	    const $rootScope = $injector.get('$rootScope');
	    if (!cause) cause = 'Application error';
	    $rootScope.$broadcast('handler:exception', { exception, cause });

	    // FOR "SOFTER" ERRORS:
      // $log.debug(exception, cause);

  		$delegate(exception, cause);
    };
  });
})

.run(($rootScope, $state, $compile, $mdUtil, $popupWindow, UserAuth, AUTH_EVENTS) => {
  'ngInject';

  $rootScope.$on('cfpLoadingBar:started', event => $mdUtil.nextTick( tick => $compile(
    angular.element(document.getElementById('loading-spinner'))
  )($rootScope)));
  /**
   * Report on exceptions. The `handler:exception` event is fired
   * anywhere an exception is caught, and if uncaught, the AppConfig
   * block also sends this event with special delegation.
   * 
   * @function $rootScope.$on
   * @memberof AppRun
   * @param  {String}   event    The event name
   * @param  {function} callback To execute on handler call
   */
  $rootScope.$on('handler:exception', (event, error) => {
    const locals = {
      type: error.cause,
      message: error.exception.message,
      suggest: [0, 2, 1]
    }
    return $popupWindow.warning({locals});
  });

  $rootScope.$on('$stateChangeStart', (event, toState, toParams, fromState, fromParams, options) => { 
    // console.log('STATE CHANGE START', toState, toParams, fromState, fromParams, options);
    if (toState.data && toState.data.roles && !UserAuth.isAuthorized(toState.data.roles)) {
      event.preventDefault();
      if (UserAuth.isAuthenticated()) {
        // user is not allowed
        $rootScope.$broadcast(AUTH_EVENTS.notAuthorized);
      } else {
        // user is not logged in
        $rootScope.$broadcast(AUTH_EVENTS.notAuthenticated);
      }
    }
  });

  // change page title based on state
  $rootScope.$on('$stateChangeSuccess', (event, toState, toParams, fromState, fromParams) => {
    $rootScope.pageTitle = 'UOIT Virtual Tour';
    if ( toState.label ) {
      $rootScope.pageTitle += ' | ';
      $rootScope.pageTitle += toState.label;
    }
    // console.log('STATE CHANGE SUCCESS', toState, toParams, fromState, fromParams);
  });

  $rootScope.$on('$stateChangeError', (event, toState, toParams, fromState, fromParams, error) => {
    console.log('STATE CHANGE ERROR: ', error, toState, toParams, fromState, fromParams);
    const locals = {
      type: 'Navigation issue!',
      message: error.message || error.msg || 'The view you requested hit a snag while loading.',
      suggest: [4, 3]
    }
    $popupWindow.error({locals});
  });

  $rootScope.$on('$stateNotFound', (event, unfoundState, fromState, fromParams) => { 
    const locals = {
      type: 'Page not found!',
      message: `The page associated with (${unfoundState.to} : ${unfoundState.toParams}) cannot be located.`,
      suggest: [3]
    }
    $popupWindow.error({locals});
    console.log('STATE NOT FOUND', unfoundState, fromState, fromParams);
  });

  console.log('starting auth init...')
  UserAuth.initAuth().then(user => {
    $rootScope.$broadcast(AUTH_EVENTS.loginSuccess, user);
    console.log('auth init complete!')
  });
});

import template from './buttonbar.html';
import controller from './buttonbar.controller';
import './buttonbar.scss';

const buttonbarComponent = {
  template,
  controller,
  bindings: {
  	items: '<',
  	open: '<?',
  	condensed: '<?',
  	mobile: '<?'
  }
}

export default buttonbarComponent;

/**
 * The buttonbar controller is responsible for managing when the sidebar
 * menu is open and which state is active. A single button needs to perform
 * different actions with a single click depending on the current state of
 * the sidebar – those actions (the ones defined in this controller) are
 * _distinct and separate from the actions performed by the buttons themselves_,
 * which are defined in the parent controller.
 *
 * The exception is the "Enter VR" button, which is always present at the bottom of
 * the buttonbar and has its method stored on this controller.
 */
class ButtonbarController {
	constructor($scope, $document, $state, $mdSidenav, UserSession) {
		'ngInject';
		this._$scope = $scope;
		this._$document = $document;
		this._$state = $state;
		this._$mdSidenav = $mdSidenav;
		/**
		 * Getter property for whether user settings permit hint tooltips.
		 * @return {Boolean} Tooltips on (true) or off (false)
		 */
  	this.showHints = () => UserSession.settings.showHints.val;
	}

	/**
	 * Activate the state associated with a button.
	 *
	 * In the context of this controller, the buttons are only responsible for
	 * handling one of three scenarios:
	 *
	 * - If the **sidebar is closed**, clicking a button should **open the sidebar
	 * and go to the button's associated state** no matter what.
	 * - If the **sidebar is open** and the **button clicked is for the active state**,
	 * the **sidebar should close and return to the "home" state**.
	 * - If the **sidebar is open** and the **clicked button's state is different from the
	 * active state**, the **sidebar should remain open and transition to the new state**.
	 * 
	 * @param  {String} state Name of the state to transition to
	 * @return {Promise}      Represents the state of the transition
	 */
  select(state) {
    if (this.menuOpen && this._$state.includes(state)) {
      return this._$mdSidenav('right').close().then(() => {
	      return this._$state.transitionTo('home');
      });
    } else { 
	  	return this._$state.transitionTo(state).then(() => {
	      this._$mdSidenav('right').open();
      });
    }
  }

  enterVR() {
  	this._$document[0].getElementById('aframe-scene').enterVR();
  }

  $onInit() {
		this._$scope.$watch(
			() => this._$mdSidenav('right').isOpen(),
			(newVal) => {
				this.menuOpen = newVal;
			}
		);
  }

}

export default ButtonbarController;

