import { isArray } from 'angular';

/**
 * Factory constructor function to provide authentication
 * details to app on runtime.
 *
 * Contains methods for login/logout, as well as a check
 * at initialization to determine if the server already
 * has an active user session. It also has utility methods
 * for determining the status of authorization.
 * 
 * @param {object} $http       Angular's $http service
 * @param {object} UserSession The user session object/factory
 */
function UserAuthFactory($http, UserSession) {
  'ngInject';

  /**
   * Wrapper function for the `UserSession.create()`.
   * 
   * @param  {object} user User object from db
   * @return {object}      User stored into session
   */
  this.createSession = user => {
  	console.log('starting session creation...');
    return UserSession.create(user);
  }

  /**
   * Sends a small check to the server with `$http.get()` to
   * determine if there is already an active user session.
   *
   * Creates a session with the returned user; creates a blank
   * session by calling `createSesstion(false)` if no user (403)
   * is returned.
   * 
   * @return {Promise} Promise that will resolve to a user session
   */
  this.initAuth = () => {
  	console.log('checking for user...');
    return $http
      .get('/user/me')
      .then(res => {
		  	console.log('user found');
        return this.createSession(res.data.user);
      }).catch(err => {
		  	console.log('user not found');
        return this.createSession(false);
      });
  }
  // TODO: RETURN A BETTER VERSION OF THIS PROMISE? PROMISE FROM CREATESESSION?

  /**
   * Logs a user in with a username and password.
   * @param  {string} $0.username The user's email
   * @param  {string} $0.password User's keystone password
   * @return {Promise}            Promise that will resolve to a user session
   */
  this.login = ({ username, password }) => {
    return $http
      .post('/user/signin', { username, password })
      .then(res => {
        return this.createSession(res.data.user);
      });
  };
  // TODO: REJECTION HANDLING

  /**
   * Logs a user out.
   * 
   * @return {Promise} Promise that destroys session when resolved
   */
  this.logout = () => {
		return $http
			.post('/user/signout')
			.then(res =>{
				UserSession.destroy();
		  	console.log('user logged out');
			}, error => {
		  	console.log('error, user not logged out');
			});
  }
  // TODO: REJECTION HANDLING

  /**
   * Utility method to tell whether a user is authenticated
   * with a username and password (only authenticated sessions)
   * have a userId.
   * 
   * @return {string} User _id field
   */
  this.isAuthenticated = () => {
    return !!UserSession.userId;
  };

  /**
   * Utility method to check the current authorized user's
   * roles in the the application.
   *
   * If there are no `authorizedRoles` speficied for the context,
   * it is public/allowed and the method can return true. If there
   * are roles specified, they are forced into an array (if not
   * already) and checked against the array of roles attached to
   * the current user session.
   *
   * If the user's roles contain a role that is in the `authorizedRoles`
   * list, a flag that the user is authorized is set. If the user is
   * still authenticated and is also authorized, the method can
   * return true.
   * 
   * @param  {[Array<String>]|[String]} authorizedRoles A list of roles allowed
   * @return {Boolean}                 									Whether user is allowed
   */
  this.isAuthorized = authorizedRoles => {
    if (!authorizedRoles || authorizedRoles.length === 0) return true;

    if (!isArray(authorizedRoles)) {
      authorizedRoles = [authorizedRoles];
    }

    let isAuthorized = false;
    for (const role of UserSession.roles) {
    	if (authorizedRoles.indexOf(role) !== -1) {
    		isAuthorized = true;
    	}
    }
    return (this.isAuthenticated() && isAuthorized);
  };

  return this;
}

export default UserAuthFactory;

function UserResolverFactory($q, $rootScope, $state, UserSession) {
  return {
    resolve() {
      var deferred = $q.defer();
      var unwatch = $rootScope.$watch(() => UserSession.user, function (currentUser) {
        if (angular.isDefined(currentUser)) {
          if (currentUser) {
            deferred.resolve(currentUser);
          } else {
            deferred.reject();
            $state.go('login');
          }
          unwatch();
        }
      });
      return deferred.promise;
    }
  };
}

export default UserResolverFactory;

import { isDefined, equals } from 'angular';

/**
 * User session management service.
 */
class UserSessionService {
	constructor($http, $popupWindow, SettingsFactory, USER_ROLES, USER_DEFAULTS) {
	  'ngInject';
	  this.$http = $http;
	  this.$popupWindow = $popupWindow;
	  this.SettingsFactory = SettingsFactory;

		this._roles = USER_ROLES;
		this._settings = USER_DEFAULTS.settings;
		this._usage = USER_DEFAULTS.usage;

		this.user = {};
	}
	/**
	 * This function creates a new user session, which is
	 * necessary to store application settings regardless of
	 * whether there is a "real" user logged in. If there is
	 * a user, the UserSession service will:
	 * 
	 * - set the `userId` property to indicate an active user
	 * - read user roles based on their boolean values on user
	 * - push role strings into a `roles` property on UserSession
	 * 
	 * If not, the service checks for local settings and inits
	 * a "blank" user with those settings (or the app defaults).
	 * 
	 * @param  {Object} user DB user object to use for session
	 * @return {Object}      Fully-formed user object after settings
	 */
	create(user) {
		this.roles = [];
		if (user) {
			this.userId = user._id;
			this.user = user;

			const { isAdmin, isEditor, isContributor } = user;
			(isAdmin) && this.roles.push(this._roles.admin);
			(isEditor) && this.roles.push(this._roles.editor);
			(isContributor) && this.roles.push(this._roles.Contributor);

	  	console.log('created session from user');
		} else {
			/**
			 * Looks inside SettingsFactory for user data matching the pattern
			 * defined in USER_DEFAULTS; store in temporary vars.
			 *
			 * @alias _settings/_usage
			 * @memberof UserSession
			 */
			const _settings = this.SettingsFactory.get('settings'),
						_usage = this.SettingsFactory.get('usage');

			/**
			 * Init a 'blank' user with the required settings properties to
			 * avoid assignment errors (i.e. `this.user.settings[key] = ...`)
			 * 
			 * @memberof UserSession
			 * @type {Object}
			 * @prop {Object} settings The user's unparsed settings
			 * @prop {Object} usage 	 The user's unparsed usage prefs
			 */
			this.user = {
				settings: {},
				usage: {}
			}

			// Use the UserSession's getter/setters to merge the found setting
			// values into USER_DEFAULTS via the user model.
			this.settings = _settings && !equals(_settings, {}) ? _settings : this._settings;
			this.usage = _usage && !equals(_usage, {}) ? _usage : this._usage;

	  	console.log('created session from local defaults', _settings);
		}
		return this.user;
	}
	/**
	 * Checks the `userId` property to verify whether settings should
	 * save to a logged in user; if not, saves them to localStorage using
	 * the `SettingsFactory`.
	 */
	save() {
  	console.log('saving settings to user...');
		if (this.userId) {
			this.$http.post('/user/save', this.user).then(user => {
				this.$popupWindow.toast('primary', {
		    	message: 'User settings updated!',
		    	action: 'Dismiss'
		    });
		  	console.log('saved settings to user!');
			});
		} else {
			this.SettingsFactory.set('settings', this.settings);
			this.SettingsFactory.set('usage', this.usage);

	  	console.log('saved settings to local!', this.user);
		}
	}
	/**
	 * Removes user session data and nulls out roles/ID.
	 */
	destroy() {
		this.user = {
			settings: {},
			usage: {}
		};
		this.roles = null;
		this.userId = null;

  	console.log('user session destroyed');
	}
	/**
	 * Getter/setter pairs for merging user setting objects from
	 * the DB the form of:
	 * 
	 * `{ toolbarOpen: false }`
	 * 
	 * into fully-qualified app-level settings objects that look like:
	 * 
	 * ```
	 * {
	 *   toolbarOpen: {
	 *     val: false,
	 *     label: 'Toolbar open',
	 *     ...
	 *   }
	 * }
	 * ```
	 *
	 * or vice-versa for setting the user object based on app prefs.
	 *
	 * @alias settings/usage
	 * @param  {Object} [settings/usage] Incoming app settings to merge into user
	 * @return {Object} 								 Merged app settings built from user
	 */
	get settings() {
		if (this.user.settings) {
	  	Object.keys(this.user.settings).map(key => {
	  		if (this._settings[key]) this._settings[key].val = this.user.settings[key];
		  });
		  return this._settings;
		}
	}
	set settings(settings) {
	  if (isDefined(settings)) {
	  	Object.keys(settings).map(key => {
	  		if (this._settings[key]) this.user.settings[key] = settings[key].val;
		  });
		  this.save();
		}
	}
	get usage() {
		if (this.user.usage) {
	  	Object.keys(this.user.usage).map(key => {
	  		if (this._usage[key]) this._usage[key].val = this.user.usage[key];
		  });
		  return this._usage;
		}
	}
	set usage(usage) {
	  if (isDefined(usage)) {
	  	Object.keys(usage).map(key => {
	  		if (this._usage[key]) this.user.usage[key] = usage[key].val;
		  });
		  this.save();
		}
	}
}

export default UserSessionService;

function bindCompileDirective($compile) {
	'ngInject';
  return function(scope, element, attrs) {
    const ensureCompileRunsOnce = scope.$watch(
      scope => {
        return scope.$eval(attrs.compile);
      },
      value => {
        element.html(value);
        $compile(element.contents())(scope);
        ensureCompileRunsOnce();
      }
    );
  };
}

export default bindCompileDirective;

