const entityComponent = {
  template,
  controller,
  require: {
    $ngModel: 'ngModel'
  }
};

export default entityComponent;

class EntityController {
  ///////
  constructor($scope) {
  	'ngInject';
    this.$scope = $scope;
  }
  ////////
  $onInit() {
    this.$ngModel.$formatters.push(modelValue => {
      let { type, attrs } = modelValue;
      console.info('control $formatter', { type, attrs });
      return {
          type,
          attrs
      };
    });
    ////
    this.$ngModel.$render = () => {
      const { type, attrs } = this.$ngModel.$viewValue;
      console.log('control $render', { type, attrs });
      this.$scope.type = type;
      this.$scope.attrs = attrs;
    };
    ////
    this.$scope.$watch(
      () => { 
        return {
          attrs: this.$scope.attrs, type: this.$scope.type 
        }
      },
      (newVal) => {
        console.log('control $watch', newVal);
        this.$ngModel.$setViewValue({
          type: this.$scope.type,
          attrs: this.$scope.attrs
        });
      }, true
    );
    ////
    this.$ngModel.$parsers.push(viewValue => {
      let { type, attrs } = viewValue;
      console.log('control $parser', { type, attrs });
      return { type, attrs };
    });
  }
  ////////
  addAttr() {
    console.log('attr added from control');
    return this.$scope.attrs.push({prop:'', val:''});
  }
  ////////
  removeAttr(attr) {
    angular.forEach(this.$scope.attrs, (a, i) => {
      if (a.$$hashKey === attr.$$hashKey) {
        console.log('attr removed from control', a);
        return this.$scope.attrs.splice(i, 1);
      };
    });
  }
}

export default EntityController;

import template from './hotspot.html';

function hotspotDirective($state, $mapApi, $popupWindow, $analytics) {
  'ngInject';
  return {
	  template,
    restrict: 'A',
    scope: {
    	hotSpot: '='
    },
    require: '^aframeScene',
		link(scope, elem, attrs, SceneCtrl) {
			const FeatureResource = $mapApi.feature;
			const hotspotId = scope.hotSpot._id;

			//  and handlers: success...
			const viewHotspot = data => {
				const { category, desc, href, name } = data.properties || data,
							locals = { type: 'hotspot', category, desc, href, name };
				$analytics.eventTrack('click', { category: 'hotspot', label: hotspotId });
				return $popupWindow.info({ locals });
			}
			// ...and error
			const errorHandler = error => {
			  const locals = {
					type: 'Hotspot load error!',
					message: 'Looks like this hotspot is broken – darn.'
		  	}
				$popupWindow.error({ locals });
			}

			// Listen for clicks on scene link element
			scope.elemClick = () => {
				// If there's a right click active...
				if (SceneCtrl._rightClick && SceneCtrl._editable) {
					// ...open the scene link editor:
					SceneCtrl.editItem(SceneCtrl._rightClick, scope.hotSpot, SceneCtrl.hotSpots);
				} else {
					document.getElementById(`hotSpot_${hotspotId}`).emit('goto');
					if (scope.hotSpot.linked && scope.hotSpot.feature) {
						FeatureResource.get({
							id: scope.hotSpot.feature
						}).$promise
							.then(data => viewHotspot(data), errorHandler)
							.then(result => {
								// ...
							});
					} else {
						viewHotspot(scope.hotSpot)
							.then(result => {
								// ...
							});
					}
				}
			};
			// elem.on('click', elemClick);
    }
	  //
  };
}

export default hotspotDirective;

