import template from './scene.html';
import controller from './scene.controller';
import './scene.scss';

/**
 * The aframeScene component definition initializes
 * the scene's bindings for its child components as
 * well as a transclusion point for the server-rendered
 * scene editor.
 *
 * @example
 * <aframe-scene
 *   sky="{{ $ctrl.scene.sky }}"
 *   scene-links="$ctrl.scene.sceneLinks"
 *   hot-spots="$ctrl.scene.hotSpots"
 *   mobile="$ctrl.mobile">
 * </aframe-scene>
 * 
 * @prop {String} sky 				Interpolated sky url
 * @prop {Array}  sceneLinks  Array of scenelink objects
 * @prop {Array}  hotSpots		Array of hotspot object
 * @prop {Object} mobile 			Object with device info
 */
const sceneComponent = {
  controller,
 	template,
  bindings: {
    sky: '@',
    sceneLinks: '<?',
    hotSpots: '<?',
    entities: '<?',
    mobile: '<?'
  },
  transclude: {
    'editor': '?sceneEditor'
  }
};

export default sceneComponent;

import { isDefined, equals, element } from 'angular';

/**
 * The scene controller manages all top-level functionality
 * of the scene component; it is responsible for initializing
 * the DOM elements of the scene and updating them when the
 * $aframeScene service changes.
 */
class SceneController {
	/**
	 * Initializes the SceneCtrl's dependencies
	 * @param  {object} $scope       The current scope
	 * @param  {jqlite} $element     The component's <aframe-scene> element
	 * @param  {object} $aframeScene The scene service
	 * @param  {object} $aframeSky   The sky service
	 */
	constructor($scope, $element, $aframeScene, $aframeSky) {
		'ngInject';
		this.$scope = $scope;
		this.$element = $element;
		this.$aframeScene = $aframeScene;
		this.$aframeSky = $aframeSky;
	}

  /**
   * Lifecycle hook to initialize component properties; sets
   * a boolean to hold the rightclick-active flag (for when a
   * user mouseups the scene with right button) and a string
   * for the current active scene's ID.
   * 
	 * Also stores JQLite-wrapped <a-scene> and <a-assets> for
	 * appending child elements into the scene from inner components.
   */
  $onInit() {
		this._rightClick = false;
		this._isMobile = false;
		this._currentSceneId = '';

		this.$sceneEl = this.$element.find('a-scene');
		this.$assetsEl = this.$sceneEl.find('a-assets');
		this.$cameraEl = element(document.getElementById('aframe-camera'));
  }

	$onChanges(changes) {
		console.log(changes);
		if (changes.mobile.isFirstChange()) return;
		if (!this._isMobile && changes.mobile.currentValue.screen) {
			// this.$cursorEl = element(document.createElement('a-cursor'));
			// this.$cursorEl.attr({
			// 	fuse: true,
			// 	fuseTimeout: 1000,
			// 	maxDistance: 100,
			// 	material: 'color:white;'
			// });
			// this.$cameraEl
			// 	.attr({ 'look-controls': 'enabled: true;', 'reverse-look-controls': 'enabled: false;' });
			// this.$cameraEl.append(this.$cursorEl);
			this._isMobile = true;
			// console.log('mobile detected; scene adapted', this.$cameraEl, this.$cursorEl)
		}
		if (this._isMobile && !changes.mobile.currentValue.screen) {
			// this.$cursorEl.remove();
			// this.$cameraEl
			// 	.attr({
			// 		'look-controls': 'enabled: false;',
			// 		'reverse-look-controls': 'enabled: true;'
			// 	});
			this._isMobile = false;
		}
	}

  /**
   * Extracts an incoming scene object's relevant data (`id`, `sceneLinks`,
   * and `hotSpots`) and assigns them to the controller for rendering
   * on the component.
   *
   * If the server-side editor component is attached (i.e. admin user is
   * logged in), it will have mounted a `checkForDraft()` method on the
   * controller, which is called here to announce if a newly-navigated-to
   * scene has saved drafts.
   * 
   * @param {object} scene A scene object from API/database
   */
	setScene(scene) {

		const { sceneLinks, hotSpots, entities, _id } = scene;

  	this.sceneLinks = sceneLinks;
  	this.hotSpots = hotSpots;
  	this.entities = entities;
  	this._currentSceneId = _id;

    this.checkForDraft&&this.checkForDraft();
	}

  /**
   * Lifecycle hook to run a check on every digest loop cycle.
   * 
   * Checks $aframeScene's current scene object; proceeds if
   * scene is defined and calls `setScene()` on the incoming data.
   *
   * Checks if there is a sky active on $aframeSky, and if so, if
   * it is different from the current active sky. Sets the conroller's
   * sky if they differ or if the controller has no sky.
   * 
   */
  $doCheck() {
  	if (isDefined(this.$aframeScene.scene)) {
  		if (this.$aframeScene.scene._id && this.$aframeScene.scene._id !== this._currentSceneId) {
			// if (!this.sky || this.$aframeScene.sky !== this.sky) {
				// this.sky = this.$aframeScene.sky;
	  		this.setScene(this.$aframeScene.scene);
	  	}
  	} else return;
  	if (this.$aframeSky.sky) {
			if (!this.sky || this.$aframeSky.sky !== this.sky) {
				this.sky = this.$aframeSky.sky;
				console.log('scene controller detects change in sky service', this.sky);
	  	}
  	}
  }
}

export default SceneController;

import angular from 'angular';
import sceneComponent from './scene.component';
import sceneService from './scene.service';

const sceneModule = angular.module('aframe.scene', [])

.component('aframeScene', sceneComponent)

.service('$aframeScene', sceneService)

.name;

export default sceneModule;

import { isArray, isObject } from 'angular';

/**
 * The scene service class is the lead orchestrator of the `<aframe-scene/>`
 * component's business logic. It is the application and component's main
 * interface to the current scene data.
 *
 * Scene manipulation is accomplished using the `$aframeScene.scene` getter
 * and setter property; internally, the scene data is stored in the service
 * "raw" under the property `sceneData`. The getter and setter are
 * responsible for transliterating changes into the "raw" data object and
 * back again. The setter also sets the `$aframeSky.sky` property, thus kicking
 * off a sky change from its respective service.
 *
 * @param {object} $mdToast        ng-material's toast service
 * @param {object} $aframeSky			 The sky service
 * @param {object} EDITOR_MESSAGES Constant to define toast configs
 */
class SceneService {
	constructor($mdToast, $aframeSky, EDITOR_MESSAGES) {
		'ngInject';

		this.$mdToast = $mdToast;
		this.$aframeSky = $aframeSky;

		this.toasts = {};
		Object.keys( EDITOR_MESSAGES ).forEach(type => {
			const toast = $mdToast.simple();
			const toastConfig = EDITOR_MESSAGES[type];
			Object.keys(toastConfig).forEach(prop => {
				toast[prop]( toastConfig[prop] );
			});
			this.toasts[type] = toast;
		});
	}
	/**
	 * Getter and setter pair for internal scene data.
	 * 
	 * @param  {[Object]} sceneData Incoming scene data to set
	 * @return {[Object]}						The scene's current data
	 * @example
	 * {
	 *   _id: '583f17cfd2cd0c0400072d9c',
	 *   parent: '57a8e38b12ad99fe889c95f3',
	 *   code: '2b',
	 *   name: 'Atrium 2B',
	 *   hotSpots: [ ... ],
	 *   sceneLinks: [ ... ],
	 *   panorama: { ... }
	 * }
	 */
	get scene() {
		if (this.sceneData) {
			return this.sceneData;
		}
	}
	set scene(sceneData) {
		this.$aframeSky.sky = sceneData.panorama;
		this.sceneData = this.lastPublished = sceneData; // { _id, sceneLinks, hotSpots, sky };
	}

	/**
	 * Method for `Array.splice()`ing items out of scene elements (`sceneLinks`,
	 * `hotSpots`) for removal from the active scene (what is being viewed, not
	 * necessarily what is saved on the server).
	 *
	 * Confirms with the user before deleting, saves a draft after, and
	 * offers to publish once removed (which will commit the changes).
	 *
	 * @example
	 * $aframeScene.removeItemFrom(
	 *   { name: 'thing' },
	 *   [{ name: 'thing' }, { name: 'thung' }, { name: 'thong' }],
	 *   () => this.updateView()
	 * );
	 * // ...results in:
	 * // >> [{ name: 'thung' }, { name: 'thong' }]
	 * 
	 * @param  {object}   		 item       The item being removed
	 * @param  {Array<Object>} collection The array it will be removed from
	 * @param  {Function} 		 cb         Callback to run when item removed
	 * @return {Promise}									Promise resolves to result of toast
	 */
  removeItemFrom(item, collection, cb) {
    this.$mdToast.show(this.toasts.confirm).then(response => {
      if ( response == 'ok' ) {
			  const index = collection.indexOf(item);
			  if (index !== -1) {
			  	collection.splice(index, 1);
	      	cb&&cb();
	      	return this.$mdToast.show(this.toasts.itemRemoved);
			  } 
      }
    }).then(response => {
      if ( response == 'ok' ) {
      	this.$aframeEditor.publish();
      }
    });
	}

	/**
	 * Method for `Array.push()`ing items onto the list of current scene
	 * elements (`sceneLinks`, `hotSpots`) for display in the scene.
	 *
	 * Checks validity of collection and item; adds item and informs user
	 * of the newly added item's default position. Offers to publish changes.
	 * 
	 * @param {Object}   			item       The item being added
	 * @param {Array<Object>} collection The array it will be added to
	 * @param {Function}			cb         Callback to run when item added
	 */
	addItemTo(item, collection, cb) {
	  if (isObject(item) && isArray(collection)) {
	  	collection.push(item)
    	cb&&cb(collection);
    	if (item.position && item.position.length) {
    		this.toasts.itemAdded.textContent(`Item added at ${ item.position.join(', ') }!`);
    	}
	    this.$mdToast.show(this.toasts.itemAdded).then(response => {
	      if ( response == 'ok' ) {
			  	this.$aframeEditor.publish();
	      }
	    });
	  }
	}
}

export default SceneService;

